public boolean sameComposers (Collection otherComposers) {
    if (otherComposers.size () != composers.size ()) {
        return false;
    }
    for (Iterator it = composers.iterator (); it.hasNext ();) {
        Object object = it.next ();
        if (! otherComposers.contains (object)) {
            return false;
        }
    }
    return true;
}

