public TestXMLUtils (String testName) {
    super (testName);
}

public void testXMLNameTest () {
    assertEquals ("Testing name 'Name<'", false, XMLUtils.isWellFormedXMLName ("Name<"));
    assertEquals ("Testing name 'Name>'", false, XMLUtils.isWellFormedXMLName ("Name>"));
    assertEquals ("Testing name 'Name''", false, XMLUtils.isWellFormedXMLName ("Name'"));
    assertEquals ("Testing name 'Name_:-.'", true, XMLUtils.isWellFormedXMLName ("Name_:-."));
    assertEquals ("Testing name '.Name'", false, XMLUtils.isWellFormedXMLName (".Name"));
    assertEquals ("Testing name '-Name'", false, XMLUtils.isWellFormedXMLName ("-Name"));
    assertEquals ("Testing name ':Name'", true, XMLUtils.isWellFormedXMLName (":Name"));
    assertEquals ("Testing name '_Name'", true, XMLUtils.isWellFormedXMLName ("_Name"));
    assertEquals ("Testing name 'A0123456789Name", true, XMLUtils.isWellFormedXMLName ("A0123456789Name"));
}

public void testAttributeEscaping () {
    assertEquals ("Escaping: <", "<", XMLUtils.escapeAttributeValue ("<"));
    assertEquals ("Escaping: >", ">", XMLUtils.escapeAttributeValue (">"));
    assertEquals ("Escaping: '", "'", XMLUtils.escapeAttributeValue ("'"));
    assertEquals ("Escaping: \"", """, XMLUtils.escapeAttributeValue ("\""));
    assertEquals ("Escaping: &", "&", XMLUtils.escapeAttributeValue ("&"));
    assertEquals ("Escaping: 1<", "1<", XMLUtils.escapeAttributeValue ("1<"));
    assertEquals ("Escaping: 1>", "1>", XMLUtils.escapeAttributeValue ("1>"));
    assertEquals ("Escaping: 1'", "1'", XMLUtils.escapeAttributeValue ("1'"));
    assertEquals ("Escaping: 1\"", "1"", XMLUtils.escapeAttributeValue ("1\""));
    assertEquals ("Escaping: 1&", "1&", XMLUtils.escapeAttributeValue ("1&"));
    assertEquals ("Escaping: <2", "<2", XMLUtils.escapeAttributeValue ("<2"));
    assertEquals ("Escaping: >2", ">2", XMLUtils.escapeAttributeValue (">2"));
    assertEquals ("Escaping: '2", "'2", XMLUtils.escapeAttributeValue ("'2"));
    assertEquals ("Escaping: \"2", ""2", XMLUtils.escapeAttributeValue ("\"2"));
    assertEquals ("Escaping: &2", "&2", XMLUtils.escapeAttributeValue ("&2"));
    assertEquals ("Escaping: a<b", "a<b", XMLUtils.escapeAttributeValue ("ab", "a>b", XMLUtils.escapeAttributeValue ("a>b"));
    assertEquals ("Escaping: a'b", "a'b", XMLUtils.escapeAttributeValue ("a'b"));
    assertEquals ("Escaping: a\"b", "a"b", XMLUtils.escapeAttributeValue ("a\"b"));
    assertEquals ("Escaping: a&b", "a&b", XMLUtils.escapeAttributeValue ("a&b"));
    assertEquals ("Escaping: <<abba", "<<abba", XMLUtils.escapeAttributeValue ("<>abba", ">>abba", XMLUtils.escapeAttributeValue (">>abba"));
    assertEquals ("Escaping: ''abba", "''abba", XMLUtils.escapeAttributeValue ("''abba"));
    assertEquals ("Escaping: \"\"abba", """abba", XMLUtils.escapeAttributeValue ("\"\"abba"));
    assertEquals ("Escaping: &&abba", "&&abba", XMLUtils.escapeAttributeValue ("&&abba"));
    assertEquals ("Escaping: a<>b''c\"e>f'&g", "a<>b''c"e>f'&g", XMLUtils.escapeAttributeValue ("a<>b''c\"e>f'&g"));
}

public void testCDATAEscaping () {
    assertEquals ("Escaping: <", "<", XMLUtils.escapeCDATAContent ("<"));
    assertEquals ("Escaping: >", ">", XMLUtils.escapeCDATAContent (">"));
    assertEquals ("Escaping: '", "'", XMLUtils.escapeCDATAContent ("'"));
    assertEquals ("Escaping: \"", "\"", XMLUtils.escapeCDATAContent ("\""));
    assertEquals ("Escaping: &", "&", XMLUtils.escapeCDATAContent ("&"));
    assertEquals ("Escaping: ]]", "]]", XMLUtils.escapeCDATAContent ("]]"));
    assertEquals ("Escaping: ]>", "]>", XMLUtils.escapeCDATAContent ("]>"));
    assertEquals ("Escaping: ]]>", "]]>", XMLUtils.escapeCDATAContent ("]]>"));
    assertEquals ("Escaping: ]]>]]>", "]]>]]>", XMLUtils.escapeCDATAContent ("]]>]]>"));
    assertEquals ("Escaping: ]>]]>", "]>]]>", XMLUtils.escapeCDATAContent ("]>]]>"));
    assertEquals ("Escaping: ]]>]]]>", "]]>]]]>", XMLUtils.escapeCDATAContent ("]]>]]]>"));
    assertEquals ("Escaping: ", "", XMLUtils.escapeCDATAContent (""));
}

