public void render (OutputStream stream) {
    try {
        render (stream, null);
    } catch (UnsupportedEncodingException e) {
    }
}

public void render (OutputStream stream, String encoding) throws UnsupportedEncodingException {
    PrintWriter pw = null;
    if (encoding == null) {
        pw = new PrintWriter (stream);
    }
    else {
        pw = new PrintWriter (new OutputStreamWriter (stream, encoding));
    }
    render (pw, encoding);
    pw.flush ();
}

public void render (Writer writer) {
    render (writer, null);
}

public void render (Writer writer, String encoding) {
    PrintWriter pw = new PrintWriter (writer);
    render (pw, encoding);
    pw.flush ();
}

public void render (PrintWriter writer) {
    render (writer, null);
}

public void render (PrintWriter writer, String encoding) {
    writer.print ("");
    writer.println ();
    writer.println ("");
    writer.println ();
    writer.println ("");
    writer.println ();
    writer.println ("  ");
    writer.println ();
    writer.print ("    ");
    writer.print (title);
    writer.println ("");
    writer.print ("    ");
    writer.print (description);
    writer.println ("");
    writer.print ("    ");
    writer.print (link);
    writer.println ("");
    writer.print ("    ");
    writer.print (language);
    writer.println ("");
    if (rating != null) {
        writer.print ("    ");
        writer.print (rating);
        writer.println ("");
    }
    if (copyright != null) {
        writer.print ("    ");
        writer.print (copyright);
        writer.print ("");
    }
    if (pubDate != null) {
        writer.print ("    ");
        writer.print (pubDate);
        writer.println ("");
    }
    if (lastBuildDate != null) {
        writer.print ("    ");
        writer.print (lastBuildDate);
        writer.println ("");
    }
    if (docs != null) {
        writer.print ("    ");
        writer.print (docs);
        writer.println ("");
    }
    if (managingEditor != null) {
        writer.print ("    ");
        writer.print (managingEditor);
        writer.println ("");
    }
    if (webMaster != null) {
        writer.print ("    ");
        writer.print (webMaster);
        writer.println ("");
    }
    writer.println ();
    if (image != null) {
        image.render (writer);
        writer.println ();
    }
    if (textInput != null) {
        textInput.render (writer);
        writer.println ();
    }
    String skipDays [] = findSkipDays ();
    if (skipDays.length > 0) {
        writer.println ("    ");
        for (int i = 0; i < skipDays.length; i ++) {
            writer.print ("      ");
            writer.print (skipDays [i]);
            writer.println ("");
        }
        writer.println ("    ");
    }
    String skipHours [] = findSkipHours ();
    if (skipHours.length > 0) {
        writer.println ("    ");
        for (int i = 0; i < skipHours.length; i ++) {
            writer.print ("      ");
            writer.print (skipHours [i]);
            writer.println ("");
        }
        writer.println ("    ");
        writer.println ();
    }
    Item items [] = findItems ();
    for (int i = 0; i < items.length; i ++) {
        items [i].render (writer);
        writer.println ();
    }
    writer.println ("  ");
    writer.println ();
    writer.println ("");
}

