    boolean result = false;
    if (obj == this) {
        result = true;
    }
    return result;
}

public int hashCode () {
    return code;
}

public String toString () {
    String result = "[Binding]";
    switch (code) {
        case ELEMENT_CODE :
            result = "[Binding: ELEMENT]";
            break;
        case ATTRIBUTE_CODE :
            result = "[Binding: ATTRIBUTE]";
            break;
    }
    return result;
}

